BFS 篇幅问题 只给出核心代码 省略一些不必要的声明和main() BFS板子123456789101112131415161718192021222324252627282930313233343536373839404142434445464748//以输出路径的5×5迷宫为例struct point&#123;//省略重载point(x,y)和== int x, y;&#125;;const int dx[4] = &#123; 0,0,-1,1 &#125;;const int dy[4] = &#123; -1,1,0,0 &#125;;int dis[5][5];//-1表可以走,-2表不能走point path[5][5];void output(point a, point b)&#123; if (b == a) &#123; printf(&quot;(%d,%d)
&quot;, b.x, b.y); return; &#125; output(a, path[b.x][b.y]); printf(&quot;(%d,%d)
&quot;, b.x, b.y);&#125;int bfs(point a,point b)&#123; queue&lt;point&gt; q; point now = a; dis[now.x][now.y] = 0; q.push(now); while (q.size()) &#123; now = q.front(); q.pop(); if (now == b) &#123; output(a, b); return dis[now.x][now.y]; &#125; for (int i = 0; i &lt; 4; i++) &#123; int new_x = now.x + dx[i],new_y = now.y + dy[i]; if (new_x&gt;=0&amp;&amp;new_x&lt;=4 &amp;&amp; new_y&gt;=0&amp;&amp;new_y&lt;=4 &amp;&amp; dis[new_x][new_y]==-1) &#123; q.push(point(new_x, new_y)); dis[new_x][new_y] = dis[now.x][now.y] + 1; path[new_x][new_y] = now; &#125; &#125; &#125; return -1;&#125; 倒水问题 1234567891011121314151617181920212223242526272829303132333435363738394041424344454647484950515253545556575859606162636465666768697071727374757677787980int A, B, C;//map中使用struct需要重载 &lt; 进行排序struct state&#123; int a, b;//省略重载state(a,b) bool operator &lt; (const state&amp; p) const &#123; if (a != p.a)return a &lt; p.a; return b &lt; p.b; &#125;&#125;;map&lt;state, state&gt; from; map&lt;state, string&gt; step;void output(state now)&#123; if (now.a == 0 &amp;&amp; now.b == 0) &#123; //printf(&quot;&lt;%d,%d&gt;
&quot;, now.a, now.b); return; &#125; output(from[now]); //printf(&quot;-&gt;&lt;%d,%d&gt;&quot;, now.a, now.b); cout &lt;&lt; step[now] &lt;&lt; endl;&#125;void bfs()&#123; queue&lt;state&gt; q; state now(0,0); q.push(now); while (q.size()) &#123; now = q.front(); q.pop(); if (now.a == C || now.b == C) &#123; output(now); return true; &#125; //如何记录之前状态是否已经存在了呢? 类似迷宫 已经走过 //map&lt;state, state&gt; from; if (now.a &lt; A)//①fill A &#123; state after(A, now.b); if (from.find(after) == from.end()) &#123; q.push(after); from[after] = now; step[after] = &quot;fill A&quot;; &#125; &#125; //省略②empty A if (now.a &gt; 0)//③pour A B B满orA空 &#123; state after; if (now.a + now.b &lt;= B) after.a = 0, after.b = now.a + now.b; else after.a = now.a + now.b - B, after.b = B; if (from.find(after) == from.end()) &#123; q.push(after); from[after] = now; step[after] = &quot;pour A B&quot;; &#125; &#125; //省略关于B的相同操作 &#125;&#125;int main()&#123; while (cin &gt;&gt; A &gt;&gt; B &gt;&gt; C) &#123; from.clear(); step.clear(); bfs(); cout &lt;&lt; &quot;success&quot; &lt;&lt; endl; &#125; return 0;&#125; 超级源点问题 csp 201409-4 “最佳配餐” 123456789101112131415161718192021222324252627282930313233343536//初始化queue的时候将所有分店都push进去,bfs结束条件改为cnt==n*n 即网格中除了障碍物全部都遍历到.//然后每个网格点都有了最小的dis,与记录的客户数目相乘算得结果即可.//注:①ans要用long long储存②由图可知下标从1~n queue&lt;point&gt; q;void bfs()&#123; while (q.size()) q.pop(); point now; int cnt = d; for (int i = 0; i &lt; v.size(); i++) &#123;//读入的时候 vector v 记录一下分店的数目 now = v[i]; dis[now.x][now.y] = 0; q.push(now); cnt++; &#125; while (q.size()) &#123; now = q.front(); q.pop(); if (cnt == n * n) &#123; return; &#125; for (int i = 0; i &lt; 4; i++) &#123; int new_x = now.x + dx[i],new_y = now.y + dy[i]; if (new_x&gt;=1&amp;&amp;new_x&lt;=n &amp;&amp; new_y&gt;=1&amp;&amp;new_y&lt;=n &amp;&amp; dis[new_x][new_y]==-1) &#123; q.push(point(new_x, new_y)); dis[new_x][new_y] = dis[now.x][now.y] + 1; cnt++; &#125; &#125; &#125;&#125; 有特殊点的bfs csp 201403-4 “无线网络” 1234567891011121314151617181920212223242526272829303132333435363738394041424344454647484950515253545556575859606162#include&lt;iostream&gt;#include&lt;queue&gt;using namespace std;const int N = 205;struct point&#123; long long x, y; int dis,k; point() &#123; dis = k = 0; &#125; point(long long x1, long long y1, int s1, int k1) &#123; x = x1, y = y1, dis = s1, k = k1; &#125;&#125;node[N];bool vis[N];int n, m, k;long long r;int bfs(int begin, int end)&#123; queue&lt;point&gt; q; point now, after; now = node[1]; q.push(now); vis[1] = 1; while (!q.empty()) &#123; now = q.front(); q.pop(); if (now.x == node[end].x&amp;&amp;now.y == node[end].y) return now.dis - 1; //中转个数=距离-1 int len; // 下一跳可以选择node下标范围 if (now.k == k) len = n; else len = n + m; for (int i = 1; i &lt;= len; i++) &#123; if (!vis[i] &amp;&amp; ((now.x - node[i].x)*(now.x - node[i].x) + (now.y - node[i].y)*(now.y - node[i].y) &lt;= r * r)) &#123; after.x = node[i].x; after.y = node[i].y; after.dis = now.dis + 1; if (i &gt; n) after.k = now.k + 1; else after.k = now.k; q.push(after); vis[i] = 1; &#125; &#125; &#125;&#125;int main()&#123; cin &gt;&gt; n &gt;&gt; m &gt;&gt; k &gt;&gt; r; for (int i = 1; i &lt;= n + m; i++) cin &gt;&gt; node[i].x &gt;&gt; node[i].y; cout &lt;&lt; bfs(1, 2) &lt;&lt; endl; return 0;&#125;
